struct animation_frame
{
    uint8_t        pos_x;
    uint8_t        pos_y;
    uint8_t        width;
    uint8_t        height;
    uint8_t        delay;
    const uint8_t *bitmap;
};

// Animation frames
struct animation_frame frames[] = {
    {7, 48, 24, 32, 100, &bitmap_mario_0[0]}, {5, 48, 28, 30, 100, &bitmap_mario_1[0]},
    {4, 38, 32, 32, 100, &bitmap_mario_2[0]}, {4, 20, 32, 32, 100, &bitmap_mario_2[0]},
    {4, 0, 32, 32, 100, &bitmap_mario_2[0]},  {3, 10, 34, 34, 100, &bitmap_mario_3[0]},
    {2, 20, 36, 40, 100, &bitmap_mario_4[0]}, {2, 30, 36, 40, 100, &bitmap_mario_4[0]},
    {2, 40, 36, 40, 100, &bitmap_mario_4[0]}, {5, 46, 28, 34, 100, &bitmap_mario_5[0]},
    {7, 48, 24, 32, 100, &bitmap_mario_0[0]},
};

int main(void)
{
    SystemInit();
    Delay_Ms(100);
    tft_init();

    tft_set_background_color(BLACK);
    tft_fill_rect(0, 0, 160, 80, BLACK);

    uint8_t frame = 0;
    uint8_t shift = 0;

    while (1)
    {
        tft_set_color(RED);
        tft_set_cursor(94, 2);
        tft_print("Go Mario!!!");
        tft_set_color(BLUE);
        tft_set_cursor(124, 12);
        tft_print("Run!!!");
        tft_set_color(ORANGE);
        tft_set_cursor(82, 22);
        tft_print("Hit Bricks!!!");
        tft_set_color(PURPLE);
        tft_set_cursor(82, 32);
        tft_print("Beat Monsters");
        tft_set_color(PINK);
        tft_set_cursor(70, 42);
        tft_print("Rescue Princess");

        tft_draw_bitmap(frames[frame].pos_x + shift, frames[frame].pos_y, frames[frame].width, frames[frame].height,
                        frames[frame].bitmap);
        Delay_Ms(frames[frame].delay);
        tft_fill_rect(frames[frame].pos_x + shift, frames[frame].pos_y, frames[frame].width, frames[frame].height,
                      BLACK);

        if (frame != 0 && frame != 9 && frame != 10)
        {
            shift += 4;
            if (shift >= 120)
            {
                shift = 0;
            }
        }

        if (++frame >= 11)
        {
            frame = 0;
        }
    }
}
